	putchar_unlocked(snum[i--]);
	putchar_unlocked(' ');
}

void plotbefore( long index, long val)
{
	long i = index + val;
	while( i >= val)
	{
		write( i );
		i--;
	}

}

void plotafter( long index, long val, long limit)
{
	long i = val + 1;
	long counter = index + 1;
	while( counter < limit )
	{
		write( i );
		i++;
		counter++;
	}
}

