#include<iostream>
#include<cstdio>
#include<cassert>

using namespace std;

#define P 	1000000007

long long combs[201][201];
long long facts[40001],factInverse[40001];

long long x,y,temp;

void extendedGCD(int a, int b)
{
  if(b==0)
  {
	  x=1L;
	  y=0L;
  }
  else
  {
	  extendedGCD(b,a%b);
	  temp=x-y*(long long )(a/b);
	  x=y; y=temp;
  }
}

long long Inverse(long long a)
{
  extendedGCD(P,a);
  if(y<0) y+=P;
  assert(y>=0);
  return y%P;  
}

void preprocess()
{

  for(int i=0;i<=200;i++)
    for(int j=0;j<=200;j++)
    {
	    if(j>i) combs[i][j]=0;
	    else if(i+j<=0) combs[i][j]=1;
	    else
	    {
		    combs[i][j]=combs[i-1][j]+combs[i-1][j-1];
		    if(combs[i][j]>=P) combs[i][j]-=P;
	    }
    }

  facts[0]=factInverse[0]=1L;
  for(int i=1;i<=40000;i++)
  {
     facts[i]=(facts[i-1]*i)%P;
     factInverse[i]= Inverse(facts[i]);
	long long a=(facts[i]*factInverse[i])%P;
	assert(a==1);


  }
}

long long solve(int M, int N, int R)
{
  long long ans=0;
  for(int i=0;i<=M;i++)
  {
    for(int j=0;j<=N;j++)
    {
	 if(R>i*j) continue;
	long long current= (combs[M][i]*combs[N][j])%P;
	current=(current*facts[i*j])%P;
	current=(current*factInverse[i*j-R])%P;
	if( (M+N-i-j)&1) { ans-=current; if(ans<0) ans+=P;}
	else	     { ans+=current; if(ans>=P) ans-=P;}
    }
  }

  ans=(ans*factInverse[R])%P;
  return ans;
}

int main()
{
  preprocess();
  int t,m,n,r;
  scanf("%d",&t);
  while(t--)
  {
    scanf("%d%d%d",&m,&n,&r);
    printf("%lld\n",solve(m,n,r));
  }
}



