				fullLyingRangesCount++;
				lyingRanges->push_back( range(number+1,-1) );
			}else
				lyingRanges->push_back( range(number+1,1) );

			break;
	case '<':
			if(answer) 
				lyingRanges->push_back( range(number,1) );
			else {
				fullLyingRangesCount++;
				lyingRanges->push_back( range(number,-1) );
