	if (L == R) return L;
	int M = (L + R) / 2;
	for (int i = 0; i < n; ++i)
		for (int j = 0; j < m; ++j)
			dp[i+1][j+1] = -dp[i][j]+dp[i+1][j]+dp[i][j+1]+int(g[i][j] < M);

