#include<stdio.h>

#define MAXDIGITNUM 18
#define DIFFDIGITNUM 10
#define TLIM 300

long long Pow9[MAXDIGITNUM+1], Pow10[MAXDIGITNUM+1], NonZero[MAXDIGITNUM];
int Digit_In[1<<DIFFDIGITNUM][DIFFDIGITNUM+1];


void Init(void) {

	int i;

	for(Pow9[0] = Pow10[0] = 1, i = 1; i<=MAXDIGITNUM; i++) Pow9[i] = 9*Pow9[i-1], Pow10[i] = 10*Pow10[i-1];
	for(i = 1; i<MAXDIGITNUM; i++) NonZero[i] = 10*(Pow10[i]-Pow9[i]);
}


void InitDigit_In(void) {

	int istart, step, mask, lim, diff, *p, *plim;

	lim = 1<<DIFFDIGITNUM;
	for(step = lim, diff = (step>>1)*(DIFFDIGITNUM+1), istart = DIFFDIGITNUM; 1<step; step>>=1, diff>>=1, istart--)
		for(mask = 0; mask<lim; mask+=step)
			for(p = Digit_In[mask]+istart, plim = p+DIFFDIGITNUM-istart; p<=plim; p++) *(p+diff) = *p + 1;
}



long long GetNum(int &maxindex) {

	int c;
	long long val;

	while((c = getchar())<'0' || c>'9');
	val = c-'0';
	for(maxindex = 0; (c = getchar())>='0' && c<='9'; maxindex++) val = val*10 + c-'0';

	return val;
}


long long GCD(long long a,  long long b) {

	long long temp;

	while((temp = a%b)) a = b, b = temp;

	return b;
}


int DigitIn(int mask, int lim) {

	int i, val;

	for(val = i = 0; i<lim; i++) if(mask & 1<<i) val++;

	return val;
}


