	int T, i, digit, digitmask, digitnum, digit_in;
	long long N, sum, gcd;
	bool largeT = false;

	Init();
	scanf("%d", &T);
	if(TLIM<=T) InitDigit_In(), largeT = true;

	for(; T>0; T--) {

		N = GetNum(i);

		sum = -((Pow9[i+1]-1)>>3);

		for(digitmask = 0, digitnum = 1; 0<i; i--) {
			digit = (int)(N%Pow10[i+1]/Pow10[i]);
			digit_in = largeT ? Digit_In[digitmask][digit] : DigitIn(digitmask, digit);
			sum += digit*NonZero[i] + (digit*digitnum - digit_in)*Pow9[i];
			if(!(digitmask & 1<<digit)) digitmask |= 1<<digit, digitnum++;
		}

		digit = (int)(N%10);
		digit_in = largeT ? Digit_In[digitmask][digit+1] : DigitIn(digitmask, digit+1);
		sum += (digit+1)*digitnum - digit_in;

		gcd = GCD(10*N, sum);
		printf("%lld/%lld\n", sum/gcd, 10*N/gcd);
	}

	return 0;
}



