     for(int i = 0; i < MAXV; i++)
             parent[i] = i;
}

int find_set(int i){
	if(i == parent[i])
         return parent[i];

	else{
         parent[i] = find_set(parent[i]);
         return parent[i];
    }
}

void union_set(int i, int j){
    int x = find_set(i);
    int y = find_set(j);
	parent[x] = y;
}

