long long power(long long a,long long k)
{
	long long o=1;
	for(;k>0;k>>=1)
	{
		if(k&1) o=o*a%Mod;
		a=a*a%Mod;
	}
	return o;
}

long long getans(int n,int m,int s)
{
	s--;
	long long ret=1;
	if(m>n) swap(n,m);
	for(int i=1;i<=n;i++)
	{
		if(i-s>0) break;
		for(int j=1;j<=m;j++)
			ret=ret*power(i+j-1,Mod-2)%Mod;
	}
	for(int i=n;i>=1;i--)
	{
		if(i+s<=n) break;
		for(int j=1;j<=m;j++)
			ret=ret*(s+i+j-1)%Mod;
	}
	return ret;
}

