	int uE[N] = {0};
	int i, j, k;
	for(i = 0; i < n; i++)
	{
		for(j = 0; j < n; j++)
			if (!uV[j])
			{
				for(k = 0; k < n; k++)
					if (!uE[k] && fabs(a[k][j]) > eps)
						break;
				if (k < n) break;
			}
		if (j == n) throw 1;
		int var = j;
		int eq = k;
		uV[var] = i + 1;
		uE[eq] = i + 1;
		for(j = 0; j < n; j++)
			if (!uE[j] && fabs(a[j][var]) > eps)
			{
				double coef = a[j][var] / a[eq][var];
				for(k = 0; k < n; k++)
					a[j][k] -= a[eq][k] * coef;
				b[j] -= b[eq] * coef;
			}
	}

	for(i = n - 1; i >= 0; i--)
