        cin >> N >> K;
        vector<pair<double, int> > P;
        for (int i = 0; i < N; i++) {
            int p;
            cin >> p;
            P.emplace_back(p/100., i);
        }
        sort(P.begin(), P.end());
        for (int i = 0; i <= N; i++) for (int j = 0; j <= N; j++) {
            dp[i][j] = 0;
            dpr[i][j] = 0;
        }
        dp[0][0] = dpr[0][0] = 1;
        for (int i = 0; i < N; i++) for (int j = 0; j <= i; j++) {
