using namespace std;

const long long mod = 1000000007ll;

long long a[1010],b[1010],fac[1010],invfac[1010];

long long quick_pow(long long a,long long b,long long p) {
    long long ans = 1;
    for (; b > 0; b >>= 1) {
        if (b & 1)
            ans = ans * a % p;
        a = a * a % p;
    }
    return ans;
}

long long C(int n,int m) {
    return fac[n] * invfac[m] % mod * invfac[n - m] % mod;
}

long long cal(int n,int d) {
    a[0] = b[0] = 1;
    for (int i = 1; i <= n; i++) {
        a[i] = a[i - 1] * ((long long)d) % (mod - 1);
        b[i] = b[i - 1] * ((long long)(d + 1)) % (mod - 1);
    }
    long long ans = 0;
    for (int i = 0; i <= n; i++) {
        long long sum = C(n,i) * quick_pow(2,a[i] * b[n - i] % (mod - 1),mod) % mod;
        if (i & 1)
            ans = ((ans - sum) % mod + mod) % mod;
        else
            ans = (ans + sum) % mod;
    }
    return ans;
}

