	int rlt;

	if (en == st + 1) return x[st];
	if (en == st + 2) return max(x[st], x[st + 1]);

	rlt = max_val((st + 1) / 2, en / 2);
	if (st & 1) chkmax(rlt, x[st]);
	if (en & 1) chkmax(rlt, x[en - 1]);
	return rlt;
}

int range_max(int st, int en) {
	return max_val(N + st, N + en);
}

