#include <iostream>
#include <cstdio>
#include <cstring>
#include <cmath>
#include <algorithm>
#include <vector>
#include <cctype>
#include <cassert>
using namespace std;
const int maxn = 55;
typedef int MT;
//transport the top digit.
const MT mask = 0x20000035;
int n;
char buf[105];
string s[maxn][maxn];
MT m[maxn][maxn];
MT X[26];
MT mul(MT t1, MT t2){
    MT ret = 0;
    while(t1){
        if(t1&1) ret ^= t2;
        t1 >>= 1;
        t2 <<= 1;
        if((t2^mask) < t2) t2 ^= mask;
    }
    return ret;
}
MT myRand(){
    MT ret = 0;
    while((ret^mask) > ret) ret = (ret<<1)|(rand()%2);
    return ret^mask;
}
void output(){
    for(int i = 0; i < n; i ++){
        for(int j = 0; j < n; j ++){
            printf("% 9d", m[i][j]);
        }
        printf("\n");
    }
}
bool isDetZero(){
    for(int i = 0; i < n; i ++){
        int id = -1;
        for(int j = i; j < n; j ++){
            if(m[j][i] != 0){
                id = j; break;
            }
        }
        //output();
        if(id == -1) return true;
        for(int k = 0; k < n; k ++) swap(m[i][k], m[id][k]);
        for(int j = i + 1; j < n; j ++){
            MT t1 = m[i][i], t2 = m[j][i];
            for(int k = 0; k < n; k ++){
                m[j][k] = mul(m[j][k], t1) ^ mul(m[i][k], t2);
            }
        }
    }
    //printf("isF\n");
    return false;
}
