    for(int i = 0; i < 26; i ++){
        X[i] = myRand();
        //printf("%d ", X[i]);
    }
    //printf("\n");
    for(int i = 0; i < n; i ++){
        for(int j = 0; j < n; j ++){
            m[i][j] = 0;
            int p = 1;
            for(int k = 0; k < (int)s[i][j].size(); k ++){
                char c = s[i][j][k];
                if(c == '+'){
                    m[i][j] ^= p;
                    p = 1;
                }else if(c == '0') p = 0;
                else if(c == '1') p = 1;
                else{
                    assert(islower(c));
                    p = mul(p, X[c-'a']);
                }
            }
        }
    }
    return isDetZero();
}
int main() {
    srand(234);
