};

bool operator<( const pt& p, const pt& q)
{
     return ((p.x < q.x) ||
             (p.x == q.x && p.y < q.y));
}

static unsigned int n;
static vector<pt> pts;

static vector<uint32_t> bits;

static unsigned int count_bits0( uint32_t m )
{
     if (m==0) return 0;
     return 1+count_bits0(m&(m-1));
}

static unsigned int count_bits( uint32_t m )
{
     if (m >= bits.size())
     {
          bits.resize(8192);
          for (auto x=0u; x<bits.size(); x++)
          {
               bits.at(x) = count_bits0(x);
          }
     }
     return bits.at(m);
}

static double diameter( const vector<pt> &v )
{
     unsigned int r = v.size();

     vector<pt> w(2*r+1);
     w.at(0) = v.at(0);
     w.at(1) = v.at(1);

     auto j=0u;
     for (auto i=0u; i<r; i++)
     {
          while (j > 1)
          {
               int dx1 = v.at(i).x - w.at(j-2).x;
               int dy1 = v.at(i).y - w.at(j-2).y;
               int dx2 = v.at(i).x - w.at(j-1).x;
               int dy2 = v.at(i).y - w.at(j-1).y;
               if (dx1*dy2 - dy1*dx2 <= 0)
               {
                    break;
               }
               else
               { 
                    j--;
               }
          }
          w.at(j) = v.at(i);
          j++;
     }

     for (auto i=r-1; i-->0; )
     {
          while (j > 1)
          {
               int dx1 = v.at(i).x - w.at(j-2).x;
               int dy1 = v.at(i).y - w.at(j-2).y;
               int dx2 = v.at(i).x - w.at(j-1).x;
               int dy2 = v.at(i).y - w.at(j-1).y;
               if (dx1*dy2 - dy1*dx2 <= 0)
               {
                    break;
               }
               else
               { 
                    j--;
               }
          }
          w.at(j) = v.at(i);
          j++;
     }

     double ss=1000000;
     unsigned int rr = j-1;
     for (auto i=0u; i<rr; i++)
     {
          auto j = i+1;

          int dx = w.at(j).x - w.at(i).x;
          int dy = w.at(j).y - w.at(i).y;
          int d_hi = 0;
          for (auto k=0u; k<rr; k++)
          {
               if (k != i && k != j)
               {
                    int dx1 = w.at(k).x - w.at(i).x;
                    int dy1 = w.at(k).y - w.at(i).y;

                    int dd = dx1*dy - dy1*dx;
                    d_hi = max(d_hi,dd);
               }               
          }

          double s = 0;
          s = d_hi / hypot(dx,dy);

          ss = min(ss,s);
     }
     return ss;
}

static double score1( uint32_t m )
{
     unsigned int r = count_bits(m);

     if (r <= 2) return 0.0;

     vector<pt> v;
     for (uint32_t i=1u, j=0; i<=m; j++, i+=i)
     {
          if (m&i) {
               v.push_back(pts.at(j));               
          }
     }

     sort(v.begin(), v.end());

     double ss = diameter(v);

     ss /= 2.0;

     return ss;
}

//map<pair<uint32_t,uint32_t>,double> cache;
vector<vector<double> > cache;
static double score( uint32_t m, uint32_t k )
{
     if (k == 0) return 0.0;

     if (cache.at(k-1).at(m)) {
	  return cache.at(k-1).at(m)-1.0;
     }

     double aa=1000000;


     if (k == 1) {
          aa = score1(m);
     }     
     else {
          unsigned int r = count_bits(m);
          if (r <= 2*k) {
               aa = 0.0;
          }
          else
          {
               uint32_t k1 = k/2;
               uint32_t k2 = k-k1;

               uint32_t b = m&(-m);
               for (uint32_t m1=b; m1<m; )
               {
                    uint32_t m2 = m-m1;
                    if (m1 < m2) {
