                    d_hi = max(d_hi,dd);
               }               
          }

          double s = 0;
          s = d_hi / hypot(dx,dy);

          ss = min(ss,s);
     }
     return ss;
}

static double score1( uint32_t m )
{
     unsigned int r = count_bits(m);

     if (r <= 2) return 0.0;

     vector<pt> v;
     for (uint32_t i=1u, j=0; i<=m; j++, i+=i)
     {
          if (m&i) {
               v.push_back(pts.at(j));               
          }
     }

     sort(v.begin(), v.end());

     double ss = diameter(v);

     ss /= 2.0;

     return ss;
}

