vector<vector<double> > cache;
static double score( uint32_t m, uint32_t k )
{
     if (k == 0) return 0.0;

     if (cache.at(k-1).at(m)) {
	  return cache.at(k-1).at(m)-1.0;
     }

     double aa=1000000;


     if (k == 1) {
          aa = score1(m);
     }     
     else {
          unsigned int r = count_bits(m);
          if (r <= 2*k) {
               aa = 0.0;
          }
          else
          {
               uint32_t k1 = k/2;
               uint32_t k2 = k-k1;

               uint32_t b = m&(-m);
               for (uint32_t m1=b; m1<m; )
