                              double a = max(score(m1,k1), score(m2,k2));

                              aa = min(aa,a);
                         }
                    }

                    b = (m - m1);
                    b &= (-b);
                    m1 |= b;
                    m1 &= ~(b-1);
               }
          }
     }

     cache.at(k-1).at(m) = aa+1.0;

     return aa;
}

