    pair < int, int > st = all[where];
    q.push(all[where]);
    was[q.front().first][q.front().second] = 1;
    while (!q.empty()) {
      int x = q.front().first, y = q.front().second;
      q.pop();
      for (int dx = -1; dx <= +1; ++dx) {
        for (int dy = -1; dy <= +1; ++dy) {
