          pred[nx][ny] = make_pair(x, y);
          was[nx][ny] = 1;
          q.push(make_pair(nx, ny));
        }
      }
    }
    vector < pair < int, int > > pth;
    pair < int, int > fi = cap;
    while (fi != st) {
      pair < int, int > nxt = pred[fi.first][fi.second];
      pth.push_back(make_pair(fi.first - nxt.first, fi.second - nxt.second));
      fi = nxt;
    }
    reverse(pth.begin(), pth.end());
    vector < pair < int, int > > nall;
    for (int it = 0; it < (int)all.size(); ++it) {
