      bool ok = false;
      for (int i = 0; i < (int)pth.size() && !ok; ++i) {
        int dx = pth[i].first, dy = pth[i].second;
        if (s[cur.first + dx][cur.second + dy] == '*') {
          continue;
        }
        cur.first += dx;
        cur.second += dy;
        if (s[cur.first][cur.second] == 'C') {
          ok = true;
        }
      }
      if (!ok) {
        nall.push_back(cur);
      }
    }
    for (int i = 0; i < (int)pth.size(); ++i) {
      if (pth[i].first == 1) {
        res += "D";
      } else if (pth[i].second == 1) {
        res += "R";
      } else if (pth[i].first == -1) {
        res += "U";
      } else {
        res += "L";
      }
    }
    all = nall;
  }
  cout << res << '\n';
  return 0;
} 



