int targ;

struct segtree {
	int m, N;
	vector <int> sum, fl;
	vector <ii> mx;

	void Union(int v)
	{
		sum[v] = sum[2 * v] + sum[2 * v + 1];
		mx[v] = max(mx[2 * v], mx[2 * v + 1]);
	}

	void Inc(int v, int am)
	{
		if (mx[v].first != -Inf) {
			fl[v] += am; mx[v].first += am;
		}
	}

	void Down(int v)
	{
		if (fl[v]) {
			Inc(2 * v, fl[v]);
			Inc(2 * v + 1, fl[v]);
			fl[v] = 0;
		}
	}

	void Init(int v, int l, int r, const vector <int> &C)
	{
		fl[v] = 0;
		if (l == r)
