	void Init(const vector <int> &C) { Init(1, 0, N - 1, C); }

	void Change(int n)
	{
		N = n;
		m = 1;
		while (m <= 4 * n) m *= 2;
		sum.resize(m); fl.resize(m);
		mx.resize(m);
	}

	segtree(int n = 1) { Change(n); }

