		while (i <= k && j-i+1 >= minLen) {
			ok[i] = true;
			up(i, 1);
			++i;
		}
		ans += getC(0, -maxA[j] + minA[j] + j);
		ans += getC(1, -maxA[j] + j);
		ans += getC(2, minA[j] + j);
		ans += getC(3, j);
	}
	return ans;
}


