	int k = mid;
	minA[k] = maxA[k] = a[k];
	for (int i = k-1; i >= l; --i) {
		minA[i] = min(minA[i+1], a[i]);
		maxA[i] = max(maxA[i+1], a[i]);
	}
	for (int i = k+1; i <= r; ++i) {
		minA[i] = min(minA[i-1], a[i]);
		maxA[i] = max(maxA[i-1], a[i]);
	}

