	for (int j = k+1; j <= r; ++j) {
		while (imin >= l && minA[imin] >= minA[j]) {
			update(imin, true);
			--imin;
		}
		while (imax >= l && maxA[imax] <= maxA[j]) {
			update(imax, false);
			--imax;
