{   int t;
    scanf("%d\n",&t);
    while(t--)
    {   int l=0, c=0;
        char x=' ';
        while(x!='\n')
        {   scanf("%c",&x);
            int a=0;
            while(x=='.')
            {   a++;
                scanf("%c",&x);
            }
            if(a>l){l=a; c++;}
        }
        printf("%d\n",c);
    }
    return 0;
}




